function compararVersoes(version, last) {
  // Dividindo as versões em arrays de números
  const versionNumbers = version.split('.').map(Number);
  const lastNumbers = last.split('.').map(Number);

  // Comparando cada parte da versão
  for (let i = 0; i < Math.min(versionNumbers.length, lastNumbers.length); i++) {
    if (versionNumbers[i] < lastNumbers[i]) {
      return true;
    } else if (versionNumbers[i] > lastNumbers[i]) {
      return false;
    }
  }

  // Se as partes compartilhadas são iguais, a versão mais longa é maior
  return versionNumbers.length < lastNumbers.length;
}

